$(
=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=
        Logical 'nand' (Sheffer stroke)
=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=
$)

  $c -/\ $. $( 'nand' ) $)
  $( Extend wff definition to include 'nand'. $)
  wnand $a wff ( ph -/\ ps ) $.

  $( Define incompatibility ('not-and' or 'nand').  This is also called the
     Sheffer stroke, represented by a vertical bar, but we use a different
     symbol to avoid ambiguity with other uses of the vertical bar. $)
  df-nand $a |- ( ( ph -/\ ps ) <-> -. ( ph /\ ps ) ) $.

  $( Nand commutes. $)
  nandcom $p |- ( ( ph -/\ ps ) <-> ( ps -/\ ph ) ) $=
    ( wa wn wnand ancom notbii df-nand 3bitr4i ) ABCZDBACZDABEBAEJKABFGABHBAHI
    $.
    $( [10-Jan-2015] $)

  $( Introduce a right anti-conjunct to both sides of a logical equivalence. $)
  nandbi1 $p |- ( ( ph <-> ps ) -> ( ( ph -/\ ch ) <-> ( ps -/\ ch ) ) ) $=
    ( wb wa wn wnand anbi1 notbid df-nand 3bitr4g ) ABDZACEZFBCEZFACGBCGLMNABCH
    IACJBCJK $.
    $( [10-Jan-2015] $)

  $( Introduce a left anti-conjunct to both sides of a logical equivalence. $)
  nandbi2 $p |- ( ( ph <-> ps ) -> ( ( ch -/\ ph ) <-> ( ch -/\ ps ) ) ) $=
    ( wb wnand nandbi1 nandcom 3bitr3g ) ABDACEBCECAECBEABCFACGBCGH $.
    $( [10-Jan-2015] $)


  ${
    nandbi.1 $e |- ( ph <-> ps ) $.
    $( Introduce a left anti-conjunct to both sides of a logical
       equivalence. $)
    nandbi2i $p |- ( ( ch -/\ ph ) <-> ( ch -/\ ps ) ) $=
      ( wa wn wnand wb anbi2 ax-mp notbii df-nand 3bitr4i ) CAEZFCBEZFCAGCBGNOA
      BHNOHDABCIJKCALCBLM $.
      $( [10-Jan-2015] $)

    $( Introduce a right anti-conjunct to both sides of a logical
       equivalence. $)
    nandbi1i $p |- ( ( ph -/\ ch ) <-> ( ps -/\ ch ) ) $=
      ( wa wn wnand wb anbi1 ax-mp notbii df-nand 3bitr4i ) ACEZFBCEZFACGBCGNOA
      BHNOHDABCIJKACLBCLM $.
      $( [10-Jan-2015] $)
  $}

  ${
    nandbi12.1 $e |- ( ph <-> ps ) $.
    nandbi12.2 $e |- ( ch <-> th ) $.
    $( Anti-conjoin both sides of two equivalences. $)
    nandbi12i $p |- ( ( ph -/\ ch ) <-> ( ps -/\ th ) ) $=
