      $( [10-Jan-2015] $)
  $}

  ${
    nandbid.1 $e |- ( ph -> ( ps <-> ch ) ) $.
    $( Deduction adding a left anti-conjunct to both sides of a logical
       equivalence. $)
    nandbi2d $p |- ( ph -> ( ( th -/\ ps ) <-> ( th -/\ ch ) ) ) $=
      ( wa wn wnand wb anbi2 syl notbid df-nand 3bitr4g ) ADBFZGDCFZGDBHDCHAOPA
      BCIOPIEBCDJKLDBMDCMN $.
      $( [10-Jan-2015] $)

    $( Deduction adding a right anti-conjunct to both sides of a logical
       equivalence. $)
    nandbi1d $p |- ( ph -> ( ( ps -/\ th ) <-> ( ch -/\ th ) ) ) $=
      ( wa wn wnand wb anbi1 syl notbid df-nand 3bitr4g ) ABDFZGCDFZGBDHCDHAOPA
      BCIOPIEBCDJKLBDMCDMN $.
      $( [10-Jan-2015] $)

    nandbid.2 $e |- ( ph -> ( th <-> ta ) ) $.
    $( Deduction joining two equivalences to form equivalence of
